export default function GamesScreen() {
    return(
        <SafeAreaView style={styles.container}>
            <View style={styles.searchGame}>
                <TextInput
                    placeholder='Pesquisar'
                    styles={styles.input}
                    placeholderTextColor={styles.placeHolderColor.color}
                />  
                <TouchableOpacity>
                    <Ionicons name='search' size={28} style={styles.searchIcon.color}/>
                </TouchableOpacity>
            </View>

            {/* <FlatList
                data={games}
                keyExtractor={ (item) => String.id} 
                renderItem={({ item }) => <GameList data={item}/>}           
                showsVerticalScrollIndicator={false}
            /> */}
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container:{
        flex: 1,
        backgroundColor: '#222831',
        paddingTop: 36,
        paddingStart: 14,
        paddingEnd: 14,
    },
    searchGame:{
        width: '100%',
        borderRadius: 16,
        backgroundColor: '#3c3c3c',
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginTop: 16,
        marginBottom: 16,
        paddingLeft: 8,
        paddingRight: 8,
    },
    input:{
        width: '90%',
        maxWidth: '90%',
        height: 54,
    },
    placeHolderColor: {
        color: '#666666'
    },
    searchIcon:{
        color: '#666666'
    }
})
